function setup(){createCanvas(windowWidth,windowHeight),noFill(),colorMode(RGB),colorSetterA=color(0,0,0),colorSetterB=color(0,0,0),gradientControl=new gradientController,gui=new dat.GUI;var r=gui.addFolder("color A");r.add(gradientControl,"redA",0,254),r.add(gradientControl,"greenA",0,254),r.add(gradientControl,"blueA",0,254);var r=gui.addFolder("color B");r.add(gradientControl,"redB",0,254),r.add(gradientControl,"greenB",0,254),r.add(gradientControl,"blueB",0,254),gui.remember(gradientControl)}function draw(){setGradient(0,0,windowWidth,windowHeight,colorSetterA,colorSetterB,X_AXIS);var r=gradientControl.redA,o=gradientControl.greenA,e=gradientControl.blueA,t=gradientControl.redB,n=gradientControl.greenB,d=gradientControl.blueB;colorSetterA=color(r,o,e),colorSetterB=color(t,n,d)}function setGradient(r,o,e,t,n,d,l){if(l==Y_AXIS)for(var i=o;i<=o+t;i++){var a=map(i,o,o+t,0,1),g=lerpColor(n,d,a);stroke(g),line(r,i,r+e,i)}else if(l==X_AXIS)for(var i=r;i<=r+e;i++){var a=map(i,r,r+e,0,1),g=lerpColor(n,d,a);stroke(g),line(i,o,i,o+t)}}function windowResized(){resizeCanvas(windowWidth,windowHeight)}var Y_AXIS=1,X_AXIS=2,colorSetterA,colorSetterB;